import React, { useEffect, useState } from 'react';
import { View,Text,Image, ScrollView, Dimensions, TouchableOpacity, TouchableHighlight } from 'react-native';
import 'localstorage-polyfill'; 
import axios from "axios";
import Icon from 'react-native-vector-icons/Ionicons';
import * as Font from 'expo-font';
import AppLoading from 'expo-app-loading';

const India = ({navigation}) => {

  const [news,setNews] = useState([]);
  const [save,setsave] = useState(false);
  const [font,setfont] = useState(false);

  useEffect(() => {
    axios.post("http://192.168.200.32:4000/getAllIndia")
    .then(res => {
      setNews(res.data);
    })
  },[])

  const width = Dimensions.get('window').width;

  const fetchFont = () => {
    Font.loadAsync({
      "Roboto": require("../../assets/fonts/Roboto-ThinItalic.ttf")
    })
  }

  if(!font) {
    return (
      <AppLoading
      startAsync={fetchFont}
      onError={() => console.log("Error")}
      onFinish={() => {
        setfont(true);
      }}
      />
    )
  }

  return (
    <ScrollView style={{width:width,paddingHorizontal:10}}>
      <Text style={{fontWeight:"bold",fontSize:18,color:"red",marginVertical:10}}>Home / <Text style={{color:"black"}}>India</Text></Text>
      <Text style={{fontSize:20,fontWeight:"bold",backgroundColor:"#c5c0c094",paddingLeft:20,borderLeftWidth:15,borderLeftColor:"red",marginVertical:5,paddingVertical:5}}>INDIA NEWS</Text>
      { news.map((itm) => (
        <View key={itm.id} style={{marginVertical:15,borderBottomWidth:0.6,borderBottomColor:"gray",paddingBottom:10}}>
          <TouchableOpacity onPress={()=>navigation.navigate('content', itm)} style={{flexDirection:"row",paddingBottom:10}}>
            <Image source={{ uri: `http://192.168.200.32:4000/${itm.image}` }} alt="mn" style={{width:120,height:70,borderRadius:4}} />
            <View style={{paddingLeft:10,width:"70%"}}>
              <Text style={{fontSize:18,fontWeight:"600",color:"black",fontFamily: "Roboto"}}>{itm.heading}</Text>
            </View>
          </TouchableOpacity>
          <View style={{flexDirection:"row",justifyContent:"space-between"}}>
            <Text style={{color:"gray",marginTop:7}}>{itm.createdAt}</Text>
            <View style={{flexDirection:"row"}}>
              <TouchableOpacity
              activeOpacity={0.6}
              onPress={() => setsave(!save)}
              >
                { save ? <Icon name="bookmark" style={{color:"red",fontSize:25,marginRight:15}} /> : <Icon name="bookmark-outline" style={{color:"gray",fontSize:25,marginRight:15}} /> }
              </TouchableOpacity>
              <TouchableOpacity
              >
                <Icon name="share-social-outline" style={{color:"gray",fontSize:25,marginRight:10}} />
              </TouchableOpacity>
            </View>
          </View>
        </View>
      )) }
    </ScrollView>
  );
};

export default India;
