import React, { useState } from 'react';
import { View,Text,Image,ScrollView } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';

export const Custome = ({navigation}) => {
    const [tabs,setTabs] = useState([
        {id:1,name:"Home",navi:"bottom"},
        {id:2,name:"E_Paper",navi:"epaper"},
    ])
  return (
    <View style={{flex:1}}>
      <View style={{flex:0.1,flexDirection:'row',paddingTop:25,justifyContent:"center",alignItems:"center"}}>
        <Text style={{color:"black",fontSize:25,marginTop:5,fontWeight:"600"}}>The Taiping Times</Text>
      </View>
      <View style={{height:0.5,backgroundColor:"gray",marginTop:10}}></View>
      <ScrollView style={{flex:0.9,padding:20}}>
          { tabs.map(item => (
            <TouchableOpacity key={item.id} onPress={() => {navigation.navigate(`${item.navi}`)}}>
                <Text style={{fontSize:18,fontWeight:"700",letterSpacing:1,paddingBottom:15}} >{item.name}</Text>
            </TouchableOpacity>
          )) }
      </ScrollView>
    </View>
  );
};
