import React from 'react';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
import TopNews from '../pages/topnews';
import India from '../pages/india';
import Opinion from '../pages/opinion';
import { Dimensions } from 'react-native';
import Sports from '../pages/sports';
import Nation from '../pages/nation';
import Covid from '../pages/covid';
import Videos from '../pages/Videos';

const Tab = createMaterialTopTabNavigator();

const { width,height } = Dimensions.get('window');

const TopBar = (props) => {
  return (
    <Tab.Navigator screenOptions={{tabBarLabelStyle:{fontWeight:"bold",fontSize:14}}}>
      <Tab.Screen name="Popular" component={TopNews} />
      <Tab.Screen name="Taiping" component={India} />
      <Tab.Screen name="Nation" component={Nation} />
      <Tab.Screen name="Covid19" component={Covid} />
      <Tab.Screen name="Videos" component={Videos} />
    </Tab.Navigator>
  );
};

export default TopBar;
