import React, { useEffect, useState } from 'react';
import { View,Text,Image, ScrollView, Dimensions, TouchableOpacity, ImageBackground } from 'react-native';
import 'localstorage-polyfill'; 
import axios from "axios";
import Icon from 'react-native-vector-icons/Ionicons';

const Popular = ({navigation}) => {

  const [news,setNews] = useState([]);
  const [save,setsave] = useState(false);
  const [save1,setsave1] = useState(false);

  useEffect(() => {
    axios.post("http://192.168.200.32:4000/getAllIndia")
    .then(res => {
      setNews(res.data);
    })
  },[])

  const width = Dimensions.get('window').width;

  return (
    <ScrollView style={{width:width}}>
      <ScrollView horizontal={true}>
      { news.map((itm) => (
        <ImageBackground source={{ uri: `http://192.168.200.32:4000/${itm.image}` }} resizeMode="cover" key={itm.id} style={{width:width,height:200}}>
          <View style={{top:100,paddingHorizontal:10}}>
          <Text style={{color:"white",paddingBottom:15,fontSize:18,fontWeight:"600",fontFamily: "Roboto",lineHeight:25}}>Expressing regret on the Nagaland shooting, Amit Shah said all agencies</Text>
          <View style={{flexDirection:"row",justifyContent:"space-between"}}>
            <Text style={{color:"white",marginTop:7}}>{itm.createdAt}</Text>
            <View style={{flexDirection:"row"}}>
              <TouchableOpacity
              activeOpacity={0.6}
              onPress={() => setsave1(!save1)}
              >
                { save1 ? <Icon name="bookmark" style={{color:"red",fontSize:25,marginRight:15}} /> : <Icon name="bookmark-outline" style={{color:"white",fontSize:25,marginRight:15}} /> }
              </TouchableOpacity>
              <TouchableOpacity
              >
                <Icon name="share-social-outline" style={{color:"white",fontSize:25,marginRight:10}} />
              </TouchableOpacity>
            </View>
          </View>
          </View>
        </ImageBackground>
      )) }
      </ScrollView>
      { news.map((itm) => (
        <View key={itm.id} style={{marginVertical:15,borderBottomWidth:0.6,borderBottomColor:"gray",paddingBottom:10,paddingHorizontal:10}}>
          <TouchableOpacity onPress={()=>navigation.navigate('content', itm)} style={{flexDirection:"row",paddingBottom:10}}>
            <Image source={{ uri: `http://192.168.200.32:4000/${itm.image}` }} alt="mn" style={{width:120,height:70,borderRadius:4}} />
            <View style={{paddingLeft:10,width:"70%"}}>
              <Text style={{fontSize:18,fontWeight:"600",color:"black",fontFamily: "Roboto"}}>{itm.heading}</Text>
            </View>
          </TouchableOpacity>
          <View style={{flexDirection:"row",justifyContent:"space-between"}}>
            <Text style={{color:"gray",marginTop:7}}>{itm.createdAt}</Text>
            <View style={{flexDirection:"row"}}>
              <TouchableOpacity
              activeOpacity={0.6}
              onPress={() => setsave(!save)}
              >
                { save ? <Icon name="bookmark" style={{color:"red",fontSize:25,marginRight:15}} /> : <Icon name="bookmark-outline" style={{color:"gray",fontSize:25,marginRight:15}} /> }
              </TouchableOpacity>
              <TouchableOpacity
              >
                <Icon name="share-social-outline" style={{color:"gray",fontSize:25,marginRight:10}} />
              </TouchableOpacity>
            </View>
          </View>
        </View>
      )) }
    </ScrollView>
  );
};

export default Popular;
