import React from 'react'
import { ScrollView } from 'react-native';
import { View, Text, TouchableOpacity, Image, Dimensions } from 'react-native'
import Icon from 'react-native-vector-icons/Ionicons';
import YoutubePlayer from 'react-native-youtube-iframe';
var getYouTubeID = require('get-youtube-id');

const Content = ({ navigation,route }) => {
    let data = route.params;

    const width = Dimensions.get('window').width;

    const media = [
        {name: "logo-whatsapp"},
        {name: "logo-facebook"},
        {name: "logo-twitter"},
        {name: "share-social-outline"},
    ];

    return (
        <ScrollView>
            <View style={{flexDirection:"row",justifyContent:"space-between",backgroundColor:"white",paddingVertical:10,borderBottomColor:"gray",borderWidth:0.6}}>
                <TouchableOpacity onPress={() => navigation.goBack()}>
                    <Icon name="arrow-back-sharp" style={{color:"gray",fontSize:25,marginLeft:10}} />
                </TouchableOpacity>
                <View style={{flexDirection:"row"}}>
                    <TouchableOpacity>
                        <Icon name="bookmark-outline" style={{color:"gray",fontSize:25,marginRight:15}} />
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Icon name="ios-share-social-outline" style={{color:"gray",fontSize:25,marginRight:10}} />
                    </TouchableOpacity>
                </View>
            </View>
            <View style={{paddingHorizontal:10,paddingVertical:15,backgroundColor:"white"}}>
                <Text style={{fontSize:23,fontWeight:"bold"}}>{data.heading}</Text>
                <Text style={{fontSize:18,paddingVertical:10}}>{data.description}</Text>
                <View style={{backgroundColor:"gray",height:0.6,marginVertical:15}}></View>
                <Text style={{fontSize:18,color:"gray"}}>Written By <Text style={{color:"#3b5999"}}>{data.author}</Text></Text>
                <Text style={{fontSize:18,color:"gray"}}>Published At <Text style={{color:"gray"}}>{data.createdAt}</Text></Text>
                <Image source={{ uri: `http://192.168.200.32:4000/${data.image}` }} alt="mn" style={{width:width-20,height:300,marginVertical:20}} />
                <Text style={{marginBottom:15,fontSize:20,lineHeight:28}}>{data.content}</Text>
                <YoutubePlayer
                    height={300}
                    width={width-20}
                    play={false}
                    videoId={getYouTubeID(data.content)}
                />
                <Text style={{fontSize:18,color:"gray"}}>Start meaningfull conversations.</Text>
                <Text style={{fontSize:18,color:"gray"}}>Share now.</Text>
                <View style={{flexDirection:"row",width:width,marginBottom:20,paddingTop:15}}>
                    { media.map((item,index) => (
                        <TouchableOpacity key={index} style={{borderWidth:1,borderColor:"gray",borderRadius:100,height:45,width:45,marginRight:30}}>
                            <Icon name={item.name} style={{color:"gray",fontSize:25,marginTop:9,textAlign:"center"}} />
                        </TouchableOpacity>
                    )) }
                </View>
            </View>
        </ScrollView>
    )
}

export default Content
