import React from 'react';
import { View,Text,ScrollView } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';

export const Custome = ({navigation}) => {
    const tabs = [
        {id:1,name:"Home",navi:"bottom"},
        {id:2,name:"Popular",navi:"popular"},
        {id:3,name:"Taiping",navi:"taiping"},
        {id:4,name:"Nation",navi:"nation"},
        {id:5,name:"Covid_19",navi:"covid"},
        {id:6,name:"Videos",navi:"videos"},
    ];
  return (
    <View style={{flex:1}}>
      <View style={{flex:0.1,flexDirection:'row',paddingTop:25,justifyContent:"center",alignItems:"center"}}>
        <Text style={{color:"black",fontSize:25,marginTop:5,fontWeight:"600"}}>The Taiping Times</Text>
      </View>
      <ScrollView style={{flex:0.9,marginTop:15}}>
          { tabs.map(item => (
            <TouchableOpacity key={item.id} onPress={() => {navigation.navigate(`${item.navi}`)}} style={{borderBottomColor:"gray",borderBottomWidth:1,paddingHorizontal:15,paddingTop:15,paddingBottom:5}} >
                <Text style={{fontSize:18,fontWeight:"700",paddingBottom:15}} >{item.name}</Text>
            </TouchableOpacity>
          )) }
      </ScrollView>
    </View>
  );
};
