import React, { useEffect, useState } from 'react';
import { View,Text,Image, ScrollView, Dimensions, TouchableOpacity, ImageBackground } from 'react-native';
import 'localstorage-polyfill'; 
import axios from "axios";
import Icon from 'react-native-vector-icons/Ionicons';
import YoutubePlayer from 'react-native-youtube-iframe';
var getYouTubeID = require('get-youtube-id');

const Popular = ({navigation}) => {

  const [news,setNews] = useState([]);
  const [newsBanner,setNewsBanner] = useState([]);
  const [newsNation,setNewsNation] = useState([]);
  const [newsBannerNation,setNewsBannerNation] = useState([]);
  const [newsCovid,setNewsCovid] = useState([]);
  const [newsBannerCovid,setNewsBannerCovid] = useState([]);
  const [newsVideo,setNewsVideo] = useState([]);
  const [newsBannerVBideo,setNewsBannerVBideo] = useState([]);
  const [save,setsave] = useState(false);
  const [save1,setsave1] = useState(false);

  useEffect(() => {
    axios.post("http://192.168.200.32:4000/getAllIndia")
    .then(res => {
      setNews(res.data);
      setNewsBanner(res.data[0]);
      // for(let i=0;i<4;i++) {
      //   if(i>0) {
      //     setNews(news => [...news,res.data[i]]);
      //   }
      //   if(i === 0) {
      //     setNewsBanner(res.data[i])
      //   }
      // }
    })
  },[])

  useEffect(() => {
    axios.post("http://192.168.200.32:4000/getAllNation")
    .then(res => {
      setNewsNation(res.data);
      setNewsBannerNation(res.data[0]);
      // for(let i=0;i<4;i++) {
      //   if(i>0) {
      //     setNews(news => [...news,res.data[i]]);
      //   }
      //   if(i === 0) {
      //     setNewsBanner(res.data[i])
      //   }
      // }
    })
  },[])

  useEffect(() => {
    axios.post("http://192.168.200.32:4000/getAllCovid")
    .then(res => {
      setNewsCovid(res.data);
      setNewsBannerCovid(res.data[0]);
      // for(let i=0;i<4;i++) {
      //   if(i>0) {
      //     setNews(news => [...news,res.data[i]]);
      //   }
      //   if(i === 0) {
      //     setNewsBanner(res.data[i])
      //   }
      // }
    })
  },[])

  useEffect(() => {
    axios.post("http://192.168.200.32:4000/getAllVideo")
    .then(res => {
      //setNewsVideo(res.data);
      for(let i=0;i<4;i++) {
        setNewsVideo(newsVideo => [...newsVideo,res.data[i]]);
      }
    })
  },[])

  const width = Dimensions.get('window').width;

  return (
    <ScrollView style={{width:width}}>
      <ScrollView pagingEnabled horizontal={true} showsHorizontalScrollIndicator={false}>
      { news.map((itm) => (
        <ImageBackground source={{ uri: `http://192.168.200.32:4000/${itm.image}` }} resizeMode="cover" key={itm.id} style={{width:width,height:200}}>
